import React, {Component} from 'react';
import {Document} from 'react-pdf'
import axios from 'axios';
import {saveAs} from 'file-saver';

const routes = require('express').Router();

class PDFGenerator extends Component{

    state = {
        name: '',
    }

    handleChange = ({target: {value, name}})=> this.setState({
        [name]: value
    });

    createAndDownload = () =>{
        axios.post('https://locahost:5000/create-pdf', this.state)
        .then(()=>axios.get('https://locahost:5000/fetch-pdf', {responseType: 'blob'}))
        .then((response)=>{
            const pdfBlob = new Blob([response.data], {type:'application/pdf'});

            saveAs(pdfBlob, 'newPdf.pdf');
        });
    }


    render(){
        return(
        <div className='PDFGenerator'>
            <div>
                <h1>Hello World</h1>
            </div>
            <div>
                <input type='text' placeholder='Name' name='name' onChange={this.handleChange}></input>
            </div>
            <div>
                <button className='btn-primary' onClick={this.createAndDownload}>Download PDF</button>
            </div>
        </div>
        );
    }
}

// module.exports = routes;

export default PDFGenerator;
