import React from 'react';
// import {Document} from 'react-pdf'
import axios from 'axios';
import {saveAs} from 'file-saver';


//
// import Avatar from '@material-ui/core/Avatar';
// import Button from '@material-ui/core/Button';
// import CssBaseline from '@material-ui/core/CssBaseline';
// import TextField from '@material-ui/core/TextField';
// import FormControlLabel from '@material-ui/core/FormControlLabel';
// import Checkbox from '@material-ui/core/Checkbox';
// import Link from '@material-ui/core/Link';
// import Paper from '@material-ui/core/Paper';
// import Box from '@material-ui/core/Box';
// import Grid from '@material-ui/core/Grid';
// import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
// import Typography from '@material-ui/core/Typography';
// import { makeStyles } from '@material-ui/core/styles';
// import Image from '../assets/img/bg/img2.jpeg';


export default function PDFGenerator(){

    const state = {
        name: '',
    }

    const handleChange = ({target: {value, name}})=> this.setState({
        [name]: value
    });

    const createAndDownload = () =>{
        axios.post('https://locahost:5000/create-pdf', state)
        .then(()=>axios.get('https://locahost:5000/fetch-pdf', {responseType: 'blob'}))
        .then((response)=>{
            const pdfBlob = new Blob([response.data], {type:'application/pdf'});

            saveAs(pdfBlob, 'newPdf.pdf');
        });
    }



    // return(
    //     <div className='PDFGenerator'>
    //         <div>
    //             <h1>Hello World</h1>
    //         </div>
    //         <div>
    //             <input type='text' placeholder='Name' name='name' onChange={handleChange}></input>
    //         </div>
    //         <div>
    //             <button className='btn-primary' onClick={createAndDownload}>Download PDF</button>
    //         </div>

    //     </div>
    // );
    return (
        <div>
            <div>
                 <h1>Hello World</h1>
             </div>
             <div>
                 <input type='text' placeholder='Name' name='name' onChange={handleChange}></input>
             </div>
             <div>
                 <button className='btn-primary' onClick={createAndDownload}>Download PDF</button>
             </div>
        </div>
      );

}

