import React from 'react';
import axios from 'axios';
import TextField from '@material-ui/core/TextField';
import color from '@material-ui/core/colors/amber';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';
import { makeStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
// import DatePicker from "react-datepicker";
import {
  Row,
  Col,
  Card,
  CardHeader,
  CardBody,
  InputGroup,
  InputGroupAddon,
  InputGroupText,
  Label,
  Input,
  Button,
  Form,
  FormGroup,
  FormText,
  UncontrolledButtonDropdown,
  DropdownToggle,
  DropdownItem,
  DropdownMenu,
} from 'reactstrap';

import Page from 'components/Page';

// var email = 'jinxund@smu.edu'
// var vm_id = 46

class Simulator extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      // item: '',
      // amount: 1,
      // price: 1.5,
      // email: 'jinxund@smu.edu',
      // id: 1,
      // amount: 10,
      // date: Date.now(),

      item: '',
      amount: 1,
      price: 3,
      vm_id: '',
      email: 'jinxund@smu.edu',

      vm_id_array:[],

      orderInfo:{
          orderItem: '',
          orderAmount: 1,
          price: 3,
      },

      itemArray: [
          {
              id: 1,
              item: "Coke", 
              price: 2
          },
          {
              id: 2,
              item: "Sprite", 
              price: 3
          }
      ],

    }
    this.onSubmitId = this.onSubmitId.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.dateChangeHandler = this.dateChangeHandler.bind(this);
  }

  dateChangeHandler(e) {
    this.setState({ date: e });
  }
  onSubmitId()  {
    var allvm;
    axios.post('http://localhost:5000/vm/getallvm',{
      email: this.state.email
    })
    .then(response=>{
      if(response.data !== null){
        allvm = response.data
      }else{
        console.log('fail to get all vm datas')
      }
    })
    .then(()=>{
      this.setState({
        vm_id_array: Object.keys(allvm)
      })
      console.log(this.state.vm_id_array)
    })
  }

  getAllVM = () => {
    var allvm;
    axios.post('http://localhost:5000/vm/getallvm',{
      email: this.state.email
    })
    .then(response=>{
      if(response.data !== null){
        allvm = response.data
      }else{
        console.log('fail to get all vm datas')
      }
    })
    .then(()=>{
      return allvm;
    })
  }

  orderInfoChangeHandler = (event) =>{
    this.setState({
        [event.target.name]: event.target.value
      }
    );
    console.log(this.state)
  }

  handleChange = date => {
    this.setState({
      startDate: date
    });
  };

  confirmPurchase = () => {
    console.log(this.state.item)
    console.log(this.state.amount)

    axios.post('http://localhost:5000/simulator/order', this.state)
      .then(response => {
        if (response.data == 'success') {
          alert('purchase done!')
        } else {
          alert('fail to purchase!')
        }
      });
  }

  render() {
    return (
      <Page title="Simulator" breadcrumbs={[{ name: 'Simulator', active: true }]}>


        <Row>
          <Col md={12}>
            <Card>
              <CardHeader>Email</CardHeader>
              <CardBody>
                <TextField
                  id="half-width-text-field"
                  placeholder='xxx@smu.edu'
                  margin="normal"
                  fullWidth
                  name='email'
                  value='jinxund@smu.edu'
                  onChange={this.orderInfoChangeHandler}
                  
                />
                <Button
                  fullwidth="true"
                  variant="contained"
                  color="primary"
                  onClick={this.onSubmitId}
                >
                  submit
              </Button>
              </CardBody>
            </Card>
          </Col>
        </Row>

        <Row>
          <Col md={12}>
            <Card>
              <CardHeader>Vending Machine ID</CardHeader>
              <CardBody>
                <FormControl>
                  <InputLabel id="demo-simple-select-label">id</InputLabel>
                  <Select
                    labelId="demo-simple-select-label"
                    id="demo-simple-select"
                    name='vm_id'
                    value={this.state.vm_id}
                    onChange={this.orderInfoChangeHandler}>
                    {/* <MenuItem value={'1'}>id</MenuItem> */}
                    {
                      this.state.vm_id_array.map((row, index)=>{
                      return(
                        <MenuItem key={row} value={row}>{row}</MenuItem>
                        )
                      })
                    }
                  </Select>
                </FormControl>
              </CardBody>
            </Card>
          </Col>
        </Row>

        <Row>
          <Col md={12}>
            <Card>
              <CardHeader>Vending Machine Item</CardHeader>
              <CardBody>
                <FormControl>
                  <InputLabel id="demo-simple-select-label">Item</InputLabel>
                  <Select
                    labelId="demo-simple-select-label"
                    id="demo-simple-select"
                    onChange={this.orderInfoChangeHandler}
                    value={this.state.item}
                    name='item'>
                    {/* <MenuItem value={'Coke'}>Coke</MenuItem>
                    <MenuItem value={'Sprite'}>Sprite</MenuItem> */}
                    <MenuItem value={'Water'}>Water</MenuItem>
                    {
                      this.state.itemArray.map((row, index)=>{
                      return(
                        <MenuItem key={row.id} value={row.item}>{row.item}</MenuItem>
                        )
                      })
                    }
                  </Select>
                </FormControl>
              </CardBody>
            </Card>
          </Col>
        </Row>

        <Row>
          <Col md={12}>
            <Card>
              <CardHeader>Product Amount</CardHeader>
              <CardBody>
                <TextField
                  // id="half-width-text-field"
                  placeholder={''+this.state.orderInfo.orderAmount}
                  margin="normal"
                  name="amount"
                  value={this.state.amount}
                  onChange={this.orderInfoChangeHandler}
                  fullWidth
                  type="number"
                />

                <Button
                  fullwidth="true"
                  variant="contained"
                  color="primary"
                  onClick={this.confirmPurchase}
                >
                  submit
                </Button>
              </CardBody>
            </Card>
          </Col>
        </Row>

        <Row>
          <Col md={12}>
            <Card>
              <CardHeader>Order Date</CardHeader>
              <CardBody>
                <Form>
                  <FormGroup>

                    <Label for="gou tm">DateTime</Label>
                    <Input
                      type="date"
                      name="date"
                      id='dt'
                      placeholder="datetime placeholder"
                      onChange={this.dateChangeHandler} />
                  </FormGroup>
                </Form>
                <Button
                  fullwidth="true"
                  variant="contained"
                  color="primary"
                  onClick={this.confirmPurchase}
                >
                  submit
                 </Button>
              </CardBody>
            </Card>
          </Col>
        </Row>

      </Page>

    );
  }
}

export default Simulator;
