import React from 'react';
import axios from 'axios';
import TextField from '@material-ui/core/TextField';
import color from '@material-ui/core/colors/amber';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';
import { makeStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';

class Simulator extends React.Component{

    constructor(props){
        super();
        this.state = {
            item: '',
            amount: 1,
            price: 1.5
        }
    }

    changeHandler = () => {

    }

    increaseAmount = () => {
        this.setState(
            {
                amount: this.state.amount + 1
            }
        )
    }

    decreaseAmount = () => {
        if(this.state.amount > 1){
            this.setState(
                {
                    amount: this.state.amount - 1
                }
            )
        }
    }

    itemChange = event => {
        this.setState({ 
            [event.target.name]: event.target.value
        });
    }

    confirmPurchase = () => {
        console.log(this.state.item)
        console.log(this.state.amount)

        axios.post('http://localhost:5000/simulator/index', this.state)
        .then(response=>{
            if(response.data == 'success'){
                alert('purchase done!')
            }else{
                alert('fail to purchase!')
            }
        });
    }

    render(){
        return(
            <div>

                <div>
                    <FormControl>
                        <InputLabel id="demo-simple-select-label">Item</InputLabel>
                        <Select 
                                labelId="demo-simple-select-label"
                                id="demo-simple-select"
                                onChange={this.itemChange}
                                value={this.state.item}
                                name='item'>
                            <MenuItem value={'Coke'}>Coke</MenuItem>
                            <MenuItem value={'Sprite'}>Sprite</MenuItem>
                            <MenuItem value={'Water'}>Water</MenuItem>
                        </Select>
                    </FormControl>
                </div>

                <div>
                    <label>Quantity: </label><br />
                    <input type='text' value={this.state.amount} onChange={this.changeHandler} readOnly align='center'/>
                    <button className='btn-secondary' onClick={this.increaseAmount}>+</button>
                    <button className='btn-secondary' onClick={this.decreaseAmount}>-</button>
                </div>

                <div>
                    <button className='btn-primary' onClick={this.confirmPurchase}>Confirm purchase</button>
                </div>

            </div>
        );
    }

}

export default Simulator;
