import React from 'react';
import axios from 'axios';
import {saveAs} from 'file-saver';
import Pdfcomponent from './PdfComponent'
import Pdfcontent from '../pdfDocument/pdfcontent'
import pdf from 'html-pdf'

export default function PDFGenerator(){

    const state = {
        name: 'Zhenxuan',
    }

    const pdfTemplate = require('../pdfDocument/pdfcontent');

    const createAndDownload = ()=>{
        pdf.create(Pdfcontent(pdfTemplate(state)), {}).toFile(`${__dirname}/result.pdf`,(err)=>{
            if(err){
                console.log('create fail')
            }
        });
    };

    const handleChange = (e)=>{
        const name = e.target.name;
        const value = e.target.value;

        state[name] = value;
        console.log(state[name]);
    };

    return (
        <div>
            <Pdfcomponent name="Pdfcomponent"></Pdfcomponent>
            <input type='text' onClick={handleChange} placeholder='name'></input>
            <button className='btn-primary' onClick={createAndDownload}>Download PDF</button>
        </div>
    );

}

