import React from 'react';
import axios from 'axios';
import TextField from '@material-ui/core/TextField';
import color from '@material-ui/core/colors/amber';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';
import { makeStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
// import DatePicker from "react-datepicker";
import {
  Row,
  Col,
  Card,
  CardHeader,
  CardBody,
  InputGroup,
  InputGroupAddon,
  InputGroupText,
  Label,
  Input,
  Button,
  Form,
  FormGroup,
  FormText,
  UncontrolledButtonDropdown,
  DropdownToggle,
  DropdownItem,
  DropdownMenu,
} from 'reactstrap';

import Page from 'components/Page';

// var email = 'jinxund@smu.edu'
// var vm_id = 46

class Simulator extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      // item: '',
      // amount: 1,
      // price: 1.5,
      // email: 'jinxund@smu.edu',
      // id: 1,
      // amount: 10,
      // date: Date.now(),

      orderItem: '',
      orderAmount: 1,
      price: 3,
      vm_id: 2,
      email: 'jinxund@smu.edu',

      vm_id_array:[],

      orderInfo:{
          orderItem: '',
          orderAmount: 1,
          price: 3,
      },

      itemArray: [
          {
              id: 1,
              item: "Coke", 
              price: 2
          },
          {
              id: 2,
              item: "Sprite", 
              price: 3
          }
      ],

    }
    this.onSubmitId = this.onSubmitId.bind(this);
    this.onSubmitAmount = this.onSubmitAmount.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.dateChangeHandler = this.dateChangeHandler.bind(this);
  }

  dateChangeHandler(e) {
    this.setState({ date: e });
  }
  onSubmitId()  {
    // this.setState({
    //   email: 'nsoaudhsd@ashoufhasp;'
    // })
    var allvm;
    axios.post('http://localhost:5000/vm/getallvm',{
      email: this.state.email
    })
    .then(response=>{
      if(response.data !== null){
        allvm = response.data
      }else{
        console.log('fail to get all vm datas')
      }
    })
    .then(()=>{
      this.setState({
        vm_id_array: Object.keys(allvm)
      })
      console.log(this.state.vm_id_array)
    })
  }

  getAllVM = () => {
    var allvm;
    axios.post('http://localhost:5000/vm/getallvm',{
      email: this.state.email
    })
    .then(response=>{
      if(response.data !== null){
        allvm = response.data
      }else{
        console.log('fail to get all vm datas')
      }
    })
    .then(()=>{
      return allvm;
    })
  }

  changeHandler = (event) =>{
    console.log(event.target)
    console.log(this.state)
    this.setState({
        [event.target.name]: event.target.value
      }
    );
    console.log(this.state)
  }

  onSubmitAmount = () =>{
    console.log('click amount submmit')
    // this.setState((prevState, props)=>
    //   ({
    //     orderInfo:{
    //     ...prevState.orderInfo,
    //     orderAmount: this.state.orderInfo.orderAmount + 1
    //     },

    //     orderAmount: this.state.orderAmount + 1
    //   })
    // )
    //     console.log(this.state.orderInfo)
    //     console.log(this.state.orderInfo.orderAmount)

    // this.setState({
    //   amount: 15
    // })
  }

  handleChange = date => {
    this.setState({
      startDate: date
    });
  };

  // changeHandler = () => {

  // }

  // increaseAmount = () => {
  //   this.setState((prevState, props)=>
  //     ({
  //       orderInfo:{
  //       ...prevState.orderInfo,
  //       orderAmount: this.state.orderInfo.orderAmount + 1
  //       },

  //       orderAmount: this.state.orderAmount + 1
  //     })
  //   )
  //       console.log(this.state.orderInfo)
  //       console.log(this.state.orderInfo.orderAmount)
  // }

  // decreaseAmount = () => {
  //   if(this.state.orderInfo.orderAmount > 1){
  //     this.setState(
  //         {
  //             orderInfo:{
  //                 orderAmount: this.state.orderInfo.orderAmount - 1
  //             },

  //             orderAmount: this.state.orderAmount - 1
              
  //         }
  //     )
  //   }
  //   console.log(this.state.orderInfo)
  //   console.log(this.state.orderInfo.orderAmount)
  // }

  // itemChange = event => {
  //   this.setState({
  //     [event.target.name]: event.target.value
  //   });
  // }



  confirmPurchase = () => {
    console.log(this.state.item)
    console.log(this.state.amount)

    axios.post('http://localhost:5000/simulator/index', this.state)
      .then(response => {
        if (response.data == 'success') {
          alert('purchase done!')
        } else {
          alert('fail to purchase!')
        }
      });
  }

  render() {
    return (
      <Page title="Simulator" breadcrumbs={[{ name: 'Simulator', active: true }]}>


        <Row>
          <Col md={12}>
            <Card>
              <CardHeader>Email</CardHeader>
              <CardBody>
                <TextField
                  id="half-width-text-field"
                  placeholder='xxx@smu.edu'
                  margin="normal"
                  fullWidth
                  name='email'
                  onChange={this.changeHandler}
                  
                />
                <Button
                  fullwidth="true"
                  variant="contained"
                  color="primary"
                  onClick={this.onSubmitId}
                >
                  submit
              </Button>
              </CardBody>
            </Card>
          </Col>
        </Row>

        <Row>
          <Col md={12}>
            <Card>
              <CardHeader>Vending Machine ID</CardHeader>
              <CardBody>
                <FormControl>
                  <InputLabel id="demo-simple-select-label">id</InputLabel>
                  <Select
                    labelId="demo-simple-select-label"
                    id="demo-simple-select"
                    name='vm_id'>
                    {/* <MenuItem value={'1'}>id</MenuItem> */}
                    {
                      this.state.vm_id_array.map((row, index)=>{
                      return(
                        <MenuItem key={row} value={row}>{row}</MenuItem>
                        )
                      })
                    }
                  </Select>
                </FormControl>
              </CardBody>
            </Card>
          </Col>
        </Row>

        <Row>
          <Col md={12}>
            <Card>
              <CardHeader>Vending Machine Item</CardHeader>
              <CardBody>
                <FormControl>
                  <InputLabel id="demo-simple-select-label">Item</InputLabel>
                  <Select
                    labelId="demo-simple-select-label"
                    id="demo-simple-select"
                    onChange={this.changeHandler}
                    value={this.state.orderItem}
                    name='orderItem'>
                    {/* <MenuItem value={'Coke'}>Coke</MenuItem>
                    <MenuItem value={'Sprite'}>Sprite</MenuItem> */}
                    <MenuItem value={'Water'}>Water</MenuItem>
                    {
                      this.state.itemArray.map((row, index)=>{
                      return(
                        <MenuItem key={row.id} value={row.item}>{row.item}</MenuItem>
                        )
                      })
                    }
                  </Select>
                </FormControl>
              </CardBody>
            </Card>
          </Col>
        </Row>

        <Row>
          <Col md={12}>
            <Card>
              <CardHeader>Product Amount</CardHeader>
              <CardBody>
                <TextField
                  // id="half-width-text-field"
                  placeholder={''+this.state.orderInfo.orderAmount}
                  margin="normal"
                  name="orderAmount"
                  onChange={this.changeHandler}
                  fullWidth
                  type="number"
                />

                <Button
                  fullwidth="true"
                  variant="contained"
                  color="primary"
                  onClick={this.onSubmitAmount}
                >
                  submit
                </Button>
              </CardBody>
            </Card>
          </Col>
        </Row>

        <Row>
          <Col md={12}>
            <Card>
              <CardHeader>Product Amount</CardHeader>
              <CardBody>
                <Form>
                  <FormGroup>

                    <Label for="gou tm">DateTime</Label>
                    <Input
                      type="date"
                      name="date"
                      id='dt'
                      placeholder="datetime placeholder"
                      onChange={this.dateChangeHandler} />
                  </FormGroup>
                </Form>
                <Button
                  fullwidth="true"
                  variant="contained"
                  color="primary"
                  onClick={this.onSubmitAmount}
                >
                  submit
                 </Button>
              </CardBody>
            </Card>
          </Col>
        </Row>

      </Page>

    );
  }
}

export default Simulator;
