import React from 'react';
import axios from 'axios';
import TextField from '@material-ui/core/TextField';
import color from '@material-ui/core/colors/amber';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';
import { makeStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
// import DatePicker from "react-datepicker";
import {
  Row,
  Col,
  Card,
  CardHeader,
  CardBody,
  InputGroup,
  InputGroupAddon,
  InputGroupText,
  Label,
  Input,
  Button,
  Form,
  FormGroup,
  FormText,
  UncontrolledButtonDropdown,
  DropdownToggle,
  DropdownItem,
  DropdownMenu,
} from 'reactstrap';

import Page from 'components/Page';

var email = 'jinxund@smu.edu'
var vm_id = 46

class Simulator extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      // item: '',
      // amount: 1,
      // price: 1.5,
      // email: 'jinxund@smu.edu',
      // id: 1,
      // amount: 10,
      // date: Date.now(),

      orderItem: '',
            orderAmount: 1,
            price: 0,
            vm_id: vm_id,
            email: email,

            orderInfo:{
                orderItem: '',
                orderAmount: 1,
                price: 3,
            },

            itemArray: [
                {
                    id: 1,
                    item: "Coke", 
                    price: 2
                },
                {
                    id: 2,
                    item: "Sprite", 
                    price: 3
                }
            ],

    }
    this.onSubmitId = this.onSubmitId.bind(this);
    this.onSubmitAmount = this.onSubmitAmount.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.dateChanged = this.dateChanged.bind(this);
  }

  dateChanged(e) {
    this.setState({ date: e });
  }
  onSubmitId() {
    this.setState({
      email: 'nsoaudhsd@ashoufhasp;'
    })
  }

  onSubmitAmount() {
    this.setState({
      amount: 15
    })
  }

  handleChange = date => {
    this.setState({
      startDate: date
    });
  };

  changeHandler = () => {

  }

  increaseAmount = () => {
    this.setState(
      {
        amount: this.state.amount + 1
      }
    )
  }

  decreaseAmount = () => {
    if (this.state.amount > 1) {
      this.setState(
        {
          amount: this.state.amount - 1
        }
      )
    }
  }

  itemChange = event => {
    this.setState({
      [event.target.name]: event.target.value
    });
  }



  confirmPurchase = () => {
    console.log(this.state.item)
    console.log(this.state.amount)

    axios.post('http://localhost:5000/simulator/index', this.state)
      .then(response => {
        if (response.data == 'success') {
          alert('purchase done!')
        } else {
          alert('fail to purchase!')
        }
      });
  }

  render() {
    return (
      <Page title="Simulator" breadcrumbs={[{ name: 'Simulator', active: true }]}>


        <Row>
          <Col md={12}>
            <Card>
              <CardHeader>Email</CardHeader>
              <CardBody>
                <TextField
                  id="half-width-text-field"
                  placeholder={this.state.email}
                  margin="normal"
                  fullWidth
                />
                <Button
                  fullWidth
                  variant="contained"
                  color="primary"
                  onClick={this.onSubmitId}
                >
                  submit
              </Button>
              </CardBody>
            </Card>
          </Col>
        </Row>

        <Row>
          <Col md={12}>
            <Card>
              <CardHeader>Vending Machine ID</CardHeader>
              <CardBody>
                <FormControl>
                  <InputLabel id="demo-simple-select-label">id</InputLabel>
                  <Select
                    labelId="demo-simple-select-label"
                    id="demo-simple-select"
                    name='id'>
                    <MenuItem value={'1'}>id</MenuItem>

                  </Select>
                </FormControl>
              </CardBody>
            </Card>
          </Col>
        </Row>

        <Row>
          <Col md={12}>
            <Card>
              <CardHeader>Vending Machine Iiem</CardHeader>
              <CardBody>
                <FormControl>
                  <InputLabel id="demo-simple-select-label">Item</InputLabel>
                  <Select
                    labelId="demo-simple-select-label"
                    id="demo-simple-select"
                    onChange={this.itemChange}
                    value={this.state.item}
                    name='item'>
                    <MenuItem value={'Coke'}>Coke</MenuItem>
                    <MenuItem value={'Sprite'}>Sprite</MenuItem>
                    <MenuItem value={'Water'}>Water</MenuItem>
                  </Select>
                </FormControl>
              </CardBody>
            </Card>
          </Col>
        </Row>

        <Row>
          <Col md={12}>
            <Card>
              <CardHeader>Product Amount</CardHeader>
              <CardBody>
                <TextField
                  id="half-width-text-field"
                  placeholder={this.state.amount}
                  margin="normal"
                  fullWidth
                />

                <Button
                  fullWidth
                  variant="contained"
                  color="primary"
                  onClick={this.onSubmitAmount}
                >
                  submit
                </Button>
              </CardBody>
            </Card>
          </Col>
        </Row>

        <Row>
          <Col md={12}>
            <Card>
              <CardHeader>Product Amount</CardHeader>
              <CardBody>
                <Form>
                  <FormGroup>

                    <Label for="gou tm">DateTime</Label>
                    <Input
                      type="date"
                      name="date"
                      id='dt'
                      placeholder="datetime placeholder"
                      onChange={this.dateChanged} />
                  </FormGroup>
                </Form>
                <Button
                  fullWidth
                  variant="contained"
                  color="primary"
                  onClick={this.onSubmitAmount}
                >
                  submit
                 </Button>
              </CardBody>
            </Card>
          </Col>
        </Row>

      </Page>

    );
  }
}

export default Simulator;
