import React from 'react';
import axois from 'axios';
import TextField from '@material-ui/core/TextField';
import color from '@material-ui/core/colors/amber';
import Select from '@material-ui/core/Select';
import MenuItem from '@material-ui/core/MenuItem';
import { makeStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';

function Simulator(){

    // constructor(props){
    //     super();
    //     this.state = {
    //         item: '',
    //         amount: ''
    //     }
    // }
    const state = {
        item: '',
        amount: ''
    }

    const changeHandler = () => {

    }

    const increaseAmount = () => {
        this.setState(
            {
                amount: this.state.amount + 1
            }
        )
    }

    const decreaseAmount = () => {
        if(this.state.amount > 1){
            this.setState(
                {
                    amount: this.state.amount - 1
                }
            )
        }
    }

    const itemChange = event => {
        this.setState({
            item: event.target.value
        })
    }

    // render(){
        return(
            <div>
                <div>
                    <label>Quantity: </label><br />
                    <input type='text' value={state.amount} onChange={changeHandler} readOnly align='center'/>
                    <button className='btn-secondary' onClick={increaseAmount}>+</button>
                    <button className='btn-secondary' onClick={decreaseAmount}>-</button>
                </div>

                <div>
                    <FormControl>
                        <InputLabel id="demo-simple-select-label">Item</InputLabel>
                        <Select 
                                labelId="demo-simple-select-label"
                                id="demo-simple-select"
                                onChange={itemChange}>
                            <MenuItem value={'Coke'}>Coke</MenuItem>
                            <MenuItem value={'Sprite'}>Sprite</MenuItem>
                            <MenuItem value={'Water'}>Water</MenuItem>
                        </Select>
                    </FormControl>
                </div>

                <div>
                    <button className='btn-primary'>Confirm purchase</button>
                </div>

            </div>
        );
    // }

}

export default Simulator;
