import React from 'react';
import axios from 'axios';
import {saveAs} from 'file-saver';
import Pdfcomponent from './PdfComponent'
import Pdfcontent from '../pdfDocument/pdfcontent'
import pdf from 'html-pdf'

export default function PDFGenerator(){

    const state = {
        name: 'Zhenxuan',
    }

    const pdfTemplate = require('../pdfDocument/pdfcontent');

    const createAndDownload = (props)=>{
        pdf.create(Pdfcontent(pdfTemplate(props.name)), {}).toFile(`${__dirname}/result.pdf`,(err)=>{
            if(err){
                console.log('create fail')
                return Promise.reject();
            }
            return Promise.resolve();
        });
    };



    return (
        <div>
            <Pdfcomponent name="Pdfcomponent"></Pdfcomponent>
            <button className='btn-primary' onClick={createAndDownload(state)}>Download PDF</button>
        </div>
    );

}

